import { useState } from "react";
import Tag from "./Tag";
import { link } from "fs";



const Renderer = () => {
    const [code, setCode] = useState('');
    const handleCode = (e) => { setCode(e.target.value); }
    const tags = code.split('>' || '<')
    
    const doc = new DOMParser().parseFromString(code, "text/html");
    const HTMLArray = [...doc.body.children].map(el => el.outerHTML);

    return (
        <>
            <div className="flex flex-col w-full">
                <textarea
                    id="OrderNotes"
                    className="my-2 min-h-32 w-full rounded-lg border-gray-200 align-top shadow-sm sm:text-sm p-3"
                    placeholder="Enter HTML"
                    value={code}
                    onChange={handleCode}
                ></textarea>

                <div className="border-2 border-gray-800 min-h-32 p-3 ">
                    <ul className="flex flex-col">
                        {tags.map((tag) => <li>{tag}</li>)}
                    </ul>
                </div>
                {`${HTMLArray}`}
            </div>
        </>
    )
};



export default Renderer;
