import { useState } from "react";
import Attributes from "./Attributes";
import SubTag from "./SubTag";

const Tag = ({ tag }: { tag: any }) => {

    const [isOpen, setIsOpen] = useState(true)

    const toggleOpen = () => {
        setIsOpen(!isOpen)
    }

    return (
        <div className="flex flex-col">
            <span className="flex flex-col">
                {`<`}
                {tag.name}
                <Attributes attrs={tag.attrs} />
                {`>`}
            </span>
            {tag.children && tag.children.map((child: any) => (
                child.type === 'text' ?
                    <p className="ml-5">{child.content}</p>
                    :
                    <SubTag child={child} />

            ))}
            <span>{`</`}{tag.name}{`>`}</span>
        </div>
    )
}

export default Tag;
