import { useEffect, useRef, useState } from "react";

const Renderer = () => {
    const [code, setCode] = useState('');
    const handleCode = (e: any) => { setCode(e.target.value); }
    const myRef = useRef(null);
    useEffect(() => {
       myRef.current.innerHTML = '<span><strong>Another simple html string<strong></span>';
    }, [myRef])


    return (
        <>
            <div className="flex flex-col w-full">
                <textarea
                    id="OrderNotes"
                    className="my-2 min-h-32 w-full rounded-lg border-gray-200 align-top shadow-sm sm:text-sm p-3"
                    placeholder="Enter HTML"
                    value={code}
                    onChange={handleCode}
                ></textarea>

                <div className="border-2 border-gray-800 min-h-32 p-3" ref={myRef}>
                    {code}
                </div>

            </div>
        </>
    )
};



export default Renderer;
