import { useEffect, useRef, useState } from "react";
import { useClickOutside } from "../hooks/useClickOutside";

const Dropdown = () => {


    const elements = [
        'account',
        'wallet',
        'bonuses',
        'bets',
        'history'
    ]

    const dropdownRef = useRef(null);

    const [isDropdownOpen, setDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setDropdownOpen(!isDropdownOpen)
    }

    useClickOutside(dropdownRef, () => {
        setDropdownOpen(false);
    });

    const [element, setElement] = useState('account');

    const handleClick = (item: string) => {
        setElement(item)
        setDropdownOpen(false)
    }


    return (
        <>
            <div ref={dropdownRef} className="relative w-[240px] text-white">
                <button onClick={toggleDropdown} className="w-full bg-gradient-to-r from-[#54F4DF] to-[#2AB6FD] rounded p-[1px]">
                    <span className="w-full h-full bg-gray-800 flex uppercase justify-center items-center  rounded focus:outline-none ">
                        {element}
                    </span>
                </button>
                {isDropdownOpen && (<div className="absolute w-full flex flex-col rounded bg-[#2B2C36] border border-[#34C4F6]">
                    {elements && elements.map((item) => (
                        <button key={item} onClick={() => handleClick(item)} className="w-full flex items-center h-8 px-3 text-sm hover:bg-gray-200 uppercase">{item}</button>
                    ))}
                </div>)}
            </div>
        </>
    )
};

export default Dropdown;
