import { useRef, useState } from "react";
import { useClickOutside } from "../../hooks/useClickOutside";

const Dropdown = () => {


    const elements = [
        'account',
        'wallet',
        'bonuses',
        'bets',
        'history'
    ]

    const dropdownRef = useRef(null);

    const [isDropdownOpen, setDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setDropdownOpen(!isDropdownOpen)
    }

    useClickOutside(dropdownRef, () => {
        setDropdownOpen(false);
    });

    const [element, setElement] = useState('account');

    const handleClick = (item: string) => {
        setElement(item)
        setDropdownOpen(false)
    }

    const dropDownBorder = {
        borderWidth: ' 1px',
        borderStyle: 'solid',
        borderImage: `
        linearGradient(
          to bottom, 
          red, 
          rgba(0, 0, 0, 0)
        ) 1 100%;`

    };

    return (
        <>
            <div ref={dropdownRef} className="relative w-[240px] text-white">
                <button onClick={toggleDropdown}
                    style={dropDownBorder}
                    className="w-full h-[34px] bg-[#34C4F6] bg-opacity-20 flex uppercase justify-center items-center  rounded focus:outline-none">
                    {element}
                </button>
                {isDropdownOpen && (<div className="absolute mt-[5px] w-full flex flex-col rounded bg-[#2B2C36] border border-[#47C2E9]">
                    {elements && elements.map((item) => (
                        <button key={item} onClick={() => handleClick(item)} className="w-full h-[44px] flex items-center px-[24px] py-[14px] uppercase">{item}</button>
                    ))}
                </div>)}
            </div >
        </>
    )
};

export default Dropdown;
