import { useState } from "react";



const Renderer = () => {
    const [code, setCode] = useState('');
    const handleCode = (e: any) => { setCode(e.target.value); }
    const tags = code.match(/<[^>]+>[^<]*<\/[^>]+>/g);
    const parser = new DOMParser();
    const htmlDoc = parser.parseFromString(code, 'text/html');

    return (
        <>
            <div className="flex flex-col w-full">
                <textarea
                    id="OrderNotes"
                    className="my-2 min-h-32 w-full rounded-lg border-gray-200 align-top shadow-sm sm:text-sm p-3"
                    placeholder="Enter HTML"
                    value={code}
                    onChange={handleCode}
                ></textarea>

                <div className="border-2 border-gray-800 min-h-32 p-3 ">
                    <ul className="flex flex-col">
                        {tags && tags.map((tag) => <li className="flex">{tag}</li>)}
                    </ul>
                </div>
                {`${htmlDoc}`}
            </div>
        </>
    )
};



export default Renderer;
