const Dropdown = () => {
    return (
        <>
            <div className="relative w-[240px]">
                <button className="w-full flex text-center uppercase items-center h-8 pl-3 pr-2 border border-black focus:outline-none">
                        Dropdown
                </button>
                <div className="absolute flex flex-col w-40 mt-1 border border-black shadow-lg">
                    <button className="flex items-center h-8 px-3 text-sm hover:bg-gray-200">Item 1</button>
                    <button className="flex items-center h-8 px-3 text-sm hover:bg-gray-200">Item 2</button>
                    <button className="flex items-center h-8 px-3 text-sm hover:bg-gray-200">Item 3</button>
                    <button className="flex items-center h-8 px-3 text-sm hover:bg-gray-200">Item 4</button>
                </div>
            </div>
        </>
    )
};

export default Dropdown;
