import { useState } from "react";

const Dropdown = () => {

    const elements = [
        'account', 
        'wallet', 
        'bonuses', 
        'bets', 
        'history'
    ]

    const [element, setElement] = useState('account');
    
    const handleClick = (item: string) =>{
        setElement(item)
    }

    return (
        <>
            <div className="relative w-[240px]">
                <button className="w-full flex text-center uppercase items-center border border-black p-4 rounded focus:outline-none">
                    {element}
                </button>
                <div className="absolute flex flex-col w-40 mt-1 border border-black shadow-lg">
                    {elements && elements.map(( item ) => (
                        <button key={item} onClick={handleClick(item)} className="flex items-center h-8 px-3 text-sm hover:bg-gray-200 uppercase">{item}</button>
                    ))}
                </div>
            </div>
        </>
    )
};

export default Dropdown;
