import { useEffect, useRef, useState } from "react";


const Dropdown = () => {
    const rootEl = useRef(null);

    useEffect(() => {
        const onClick = e => rootEl.current.contains(e.target) || console.log('клик вне компонента');
        document.addEventListener('click', onClick);
        return () => document.removeEventListener('click', onClick);
    }, []);

    
    const elements = [
        'account',
        'wallet',
        'bonuses',
        'bets',
        'history'
    ]

    const [isDropdownOpen, setDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setDropdownOpen(!isDropdownOpen)
    }

    const [element, setElement] = useState('account');

    const handleClick = (item: string) => {
        setElement(item)
        setDropdownOpen(false)
    }

    return (
        <>
            <div ref={rootEl} className="relative w-[240px]">
                <button onClick={toggleDropdown} className="w-full flex text-center uppercase items-center border border-black p-4 rounded focus:outline-none">
                    {element}
                </button>
                <div className={`${isDropdownOpen ? 'absolute' : 'hidden'} " w-full flex flex-col mt-1 border border-black shadow-lg"`}>
                    {elements && elements.map((item) => (
                        <button key={item} onClick={() => handleClick(item)} className="w-full flex items-center h-8 px-3 text-sm hover:bg-gray-200 uppercase">{item}</button>
                    ))}
                </div>
            </div>
        </>
    )
};

export default Dropdown;
