import { useEffect, useRef, useState } from "react";
import { useClickOutside } from "../hooks/useClickOutside";

const Dropdown = () => {


    const elements = [
        'account',
        'wallet',
        'bonuses',
        'bets',
        'history'
    ]



    const [isDropdownOpen, setDropdownOpen] = useState(false);


    const dropdownRef = useRef()

    useEffect(() => {
        const closeDropdown = e => {
            if (!dropdownRef.current.contains(e.target)) {
                setDropdownOpen(false);
            }
        }
        document.body.addEventListener('click', closeDropdown)
        return () => document.body.removeEventListener('click', closeDropdown)
    }, [])


    const [element, setElement] = useState('account');

    const handleClick = (item: string) => {
        setElement(item)
        setDropdownOpen(false)
    }

    return (
        <>
            <div ref={dropdownRef} className="relative w-[240px]">
                <button onClick={toggleDropdown} className="w-full flex text-center uppercase items-center border border-black p-4 rounded focus:outline-none">
                    {element}
                </button>
                {isDropdownOpen && (<div className="absolute w-full flex flex-col mt-1 border border-black shadow-lg">
                    {elements && elements.map((item) => (
                        <button key={item} onClick={() => handleClick(item)} className="w-full flex items-center h-8 px-3 text-sm hover:bg-gray-200 uppercase">{item}</button>
                    ))}
                </div>)}
            </div>
        </>
    )
};

export default Dropdown;
