import { useEffect, useRef, useState } from "react";

const Tag = (tag: any) => {
    var html = new DOMParser().parseFromString(tag, "text/html");
    var divs = html.body.querySelectorAll('div');
    var tags = [];
    for (let i = 0; i < divs.length; i++) {
        tags.push(divs[i].outerHTML);
    }
    return (
        <>
            {tag}
        </>
    )
}

const Renderer = () => {
    const [code, setCode] = useState('');
    const handleCode = (e: any) => { setCode(e.target.value); }

    var html = new DOMParser().parseFromString(code, "text/html");
    var divs = html.body.querySelectorAll('div');
    var tags = [];
    for (let i = 0; i < divs.length; i++) {
        tags.push(divs[i].outerHTML);
    }

    return (
        <>
            <div className="flex flex-col w-full">
                <textarea
                    id="OrderNotes"
                    className="my-2 min-h-32 w-full rounded-lg border-gray-200 align-top shadow-sm sm:text-sm p-3"
                    placeholder="Enter HTML"
                    value={code}
                    onChange={handleCode}
                ></textarea>

                <div className="border-2 border-gray-800 min-h-32 p-3 flex flex-col">
                    <ul>
                        {tags.map((tag) => <Tag tag={tag} />)}
                    </ul>
                </div>
            </div>
        </>
    )
};



export default Renderer;
